import React, { useState } from "react";
import { TextField, Button, Typography, Paper } from "@material-ui/core";

import useStyles from "./styles";

const Form = () => {
    const [postData, setPostData] = useState({
        creator: "",
        title: "",
        message: "",
        tags: "",
        selectedFile: "",
    });
    const classes = useStyles();

    const handleSubmit = () => {};
    return (
        <Paper className={classes.paper}>
            <form
                autoComplete="off"
                noValidate
                className={classes.form}
                onSubmit={handleSubmit}
            >
                <Typography variant="h6">Creating a Memory</Typography>
                <TextField
                    name="creator"
                    variant="outlined"
                    label="Creator"
                    fullWidth
                    value={postData.creator}
                    //...postData - In every textfield, we do the same thing but only change the last property.
                    onChange={(e) =>
                        setPostData({ ...postData, creator: e.target.value })
                    }
                />
                <TextField
                    name="title"
                    variant="outlined"
                    label="Title"
                    fullWidth
                    value={postData.creator}
                    //...postData - In every textfield, we do the same thing but only change the last property.
                    onChange={(e) =>
                        setPostData({ ...postData, creator: e.target.value })
                    }
                />
                <TextField
                    name="message"
                    variant="outlined"
                    label="message"
                    fullWidth
                    value={postData.creator}
                    //...postData - In every textfield, we do the same thing but only change the last property.
                    onChange={(e) =>
                        setPostData({ ...postData, creator: e.target.value })
                    }
                />
                <TextField
                    name="tags"
                    variant="outlined"
                    label="tags"
                    fullWidth
                    value={postData.creator}
                    //...postData - In every textfield, we do the same thing but only change the last property.
                    onChange={(e) =>
                        setPostData({ ...postData, creator: e.target.value })
                    }
                />
            </form>
        </Paper>
    );
};

export default Form;
